import React from 'react';
import PropTypes from 'prop-types';
import styles from './Filter.module.css';
import FilterItem from "../FilterItem/FilterItem";
import {getFilterList} from "../../api";

class Filter extends React.Component{
    constructor(props){
        super(props);
        //this.categories = React.createRef();
        //this.ingrediences = React.createRef();
        this.state = {
            categories: getFilterList("category"),
            ingrediences: getFilterList("ingredience")
        }
    }

    render(){
        return(
            <div className="my-5">
                <div className="row">
                    <div className="col-3">
                        <input type="text" placeholder="NÃ¡zev"/>
                    </div>
                    <div className="col-3">
                        <FilterItem list={this.state.categories} name="Kategorie"/>
                    </div>
                    <div className="col-3">
                        <FilterItem list={this.state.ingrediences} name="Ingredience"/>
                    </div>
                </div>
                <div className="row">
                    <button className="btn btn-success" onClick={this.props.search}>Vyhledat</button>
                </div>
            </div>
        );
    }
}

export default Filter;
