import React from 'react';
import PropTypes from 'prop-types';
import styles from './Form.module.css';

class Form extends React.Component{
    constructor(props){
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);

        this.state = {
            message: "",
            newbook: {
                key: Date.now(),
                Title: "",
                Author: "",
                Genre :"",
                YearReleased: ""
            }
        };
    }

    handleSubmit(e) {
        console.log("Submit");
        e.preventDefault();
        this.setState({ message: 'Sending...' }, this.sendFormData);
    }

    sendFormData() {
        var formData = {
          Name: "Šlehačka",
          Category: "sladkost",
          Ingrediences: [{
              Amount: 250,
              Unit: "ml",
              Name: "smetana ke šlehání"
          }],
          Steps: ["ušlehejte", "zamrazte"]
        };
        setTimeout(() => {
            this.setState({ message: 'data sent!' });
            let obj = JSON.stringify(formData)
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: obj
            };
        }, 3000);
    }

    render(){
        return(
        <div>
            <form>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Název</label>
                    <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                </div>
                <div className="form-group">
                    <label htmlFor="exampleInputPassword1">Kategorie</label>
                    <input type="radio" className="form-control" id="polevka" name="category"/>
                    <input type="radio" className="form-control" id="hlavni" name="category"/>
                    <input type="radio" className="form-control" id="dezert" name="category"/>
                </div>
                <div className="row">
                    <div className="col-3">
                        <h3>Ingredience:</h3>
                        <div className="form-group row">
                            <label className="col-2" htmlFor="exampleInputEmail1">1</label>
                            <input type="email" className="form-control col-8" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                        </div>
                        <div className="form-group row">
                            <label className="col-2" htmlFor="exampleInputEmail1">2</label>
                            <input type="email" className="form-control col-8" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                        </div>
                        <div className="form-group row">
                            <label className="col-2" htmlFor="exampleInputEmail1">3</label>
                            <input type="email" className="form-control col-8" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                        </div>
                        <button type="submit" className="btn btn-success">Přidat ingredienci</button>
                    </div>
                    <div className="col-9">
                        <h3>Postup:</h3>
                            <div className="form-group">
                                <label htmlFor="exampleInputEmail1">Krok 1</label>
                                <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputEmail1">Krok 2</label>
                                <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputEmail1">Krok 3</label>
                                <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                            </div>
                        <div className="form-group">
                            <button type="submit" className="btn btn-success">Přidat krok</button>
                        </div>
                    </div>
                </div>
                <button type="submit" className="btn btn-primary">Vytvořit</button>
            </form>
        </div>
        );
    }
}

export default Form;
