import React from 'react';
import PropTypes from 'prop-types';
import styles from './App.module.css';
import Filter from "../Filter/Filter";
import List from "../List/List";
import {getFoods} from '../../api';
import Form from "../Form/Form";
import Detail from "../Detail/Detail";

import {
    BrowserRouter as Router,
    Link,
    Route,
    Switch,
} from 'react-router-dom';

class App extends React.Component{
    constructor(props) {
        super(props);
        this.load = this.load.bind(this);
        this.state = {
            foods: [],
            categories: ["polevky","hlavni","dezerty"]
        }
        this.load();
    }

    load(){
        getFoods().then( data => {
            this.setState({foods: data});
            }
        )
    }

    render(){
        return(
            <Router>
                <nav className="navbar navbar-expand-sm navbar-dark bg-dark">
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarsExample03"
                            aria-controls="navbarsExample03" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>

                    <div className="collapse navbar-collapse" id="navbarsExample03">
                        <ul className="navbar-nav mr-auto">
                            <li className="nav-item active">
                                <Link className="nav-link" to={{ pathname: "/list/:category", category: "polevky"}}>Polévky</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link" to="/hlavni">Hlavní chody</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link" to="/dezerty">Dezerty</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link" to="/add">Vytvořit nový</Link>
                            </li>
                            <li className="nav-item">
                                <Link className="nav-link" to="/list">Seznam</Link>
                            </li>
                        </ul>
                    </div>
                </nav>

                <div className="container mt-5">
                    <Switch>
                        <Route path="/add" component={Form} />
                        <Route path="/edit/:name" component={Form} />
                        <Route path="/list/:category" render={() => <List foods={this.state.foods} search={this.load} />} />
                        <Route path="/detail/:name" component={Detail}/>
                    </Switch>
                </div>
            </Router>
        );
    }
}

export default App;
