import React from 'react';
import styles from './Form.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faPlus, faMinus} from "@fortawesome/free-solid-svg-icons";
import IngredienceFormGroup from "../IngredienceFormGroup/IngredienceFormGroup";
import StepFormGroup from "../StepFormGroup/StepFormGroup";

class Form extends React.Component{
    constructor(props){
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.addIngredienceField = this.addIngredienceField.bind(this);
        this.removeIngredienceField = this.removeIngredienceField.bind(this);
        this.addStepField = this.addStepField.bind(this);
        this.removeStepField = this.removeStepField.bind(this);

        this.state = {
            food: {
                name: "",
                category: "",
                ingrediences: ["", ""],
                steps: ["","",""]
            }
        };
    }

    handleSubmit(e) {
        console.log("Submit");
        e.preventDefault();
        this.setState({ message: 'Sending...' }, this.sendFormData);
    }

    handleChange(e){

    }

    addIngredienceField(e) {
        let f = this.state.food;
        f.ingrediences.push("");
        this.setState({food: f});
    }
    removeIngredienceField(e){
        let f = this.state.food;
        f.ingrediences.splice(0, 1);
        this.setState({food: f});
    }

    addStepField(e){
        let f = this.state.food;
        f.steps.push("");
        this.setState({food: f});
    }
    removeStepField(e){
        let f = this.state.food;
        f.steps.splice(e.target.id, 1);
        this.setState({food: f});
    }

    sendFormData() {
        var formData = {
          Name: "Šlehačka",
          Category: "sladkost",
          Ingrediences: [{
              Amount: 250,
              Unit: "ml",
              Name: "smetana ke šlehání"
          }],
          Steps: ["ušlehejte", "zamrazte"]
        };
        setTimeout(() => {
            this.setState({ message: 'data sent!' });
            let obj = JSON.stringify(formData)
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: obj
            };
        }, 3000);
    }

    render(){
        const ingredience = this.state.food.ingrediences.map( i =>  <IngredienceFormGroup remove={this.removeIngredienceField}/>);
        const steps = this.state.food.steps.map((s,i) => <StepFormGroup id={i}/>);

        return(
        <div>
                <div className="form-group row">
                    <label className="col-2">Název</label>
                    <div className="col-10">
                        <input type="text" className="form-control" name="name" />
                    </div>
                </div>
                <fieldset className="form-group">
                    <div className="row">
                        <legend className="col-form-label col-sm-2 pt-0">Radios</legend>
                        <div className="col-sm-10">
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="category" value="polevka"/>
                                <label className="form-check-label">Polévka</label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="category" value="hlavni"/>
                                <label className="form-check-label">Hlavní chod</label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="category" value="dezert"/>
                                <label className="form-check-label">Dezert</label>
                            </div>
                        </div>
                    </div>
                </fieldset>
                <div className="row">
                    <div className="col-5">
                        <h3>Ingredience:</h3>
                        {ingredience}
                        <button className="btn btn-success row" onClick={this.addIngredienceField}>
                            <FontAwesomeIcon icon={faPlus} />
                        </button>
                    </div>
                    <div className="col-7">
                        <h3>Postup:</h3>
                        {steps}
                        <div className="form-group">
                            <button className="btn btn-danger" onClick={this.removeStepField}>
                                <FontAwesomeIcon icon={faMinus} />
                            </button>
                            <button className="btn btn-success" onClick={this.addStepField}>
                                <FontAwesomeIcon icon={faPlus} />
                            </button>
                        </div>
                    </div>
                </div>
                <button type="submit" className="btn btn-primary">Vytvořit</button>
        </div>
        );
    }
}

export default Form;
