import React from 'react';
import PropTypes from 'prop-types';
import styles from './FilterItem.module.css';

class FilterItem extends React.Component{
    constructor(props) {
        super(props);
        this.choice = React.createRef();
        this.showChoice = this.showChoice.bind(this);
    }
    showChoice(e){
        const node = this.choice.current;
        console.log(node)
        this.choice.current.classList.remove("invisible");
    }

    render() {
        return (
            <div>
                <input type="text" placeholder={this.props.name} onClick={this.showChoice}/>
                <div className="filter">
                    <div className={styles.choice + " invisible"} ref={this.choice}>
                        <div className="form-check">
                            <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
                            <label className="form-check-label" htmlFor="exampleCheck1">Mléko</label>
                        </div>
                        <div className="form-check">
                            <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
                            <label className="form-check-label" htmlFor="exampleCheck1">Mouka</label>
                        </div>
                        <div className="form-check">
                            <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
                            <label className="form-check-label" htmlFor="exampleCheck1">Máslo</label>
                        </div>

                        <button>Vyhledat</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default FilterItem;
