import React from 'react';
import PropTypes from 'prop-types';
import styles from './Detail.module.css';

class Detail extends React.Component{
    constructor(props) {
        super(props);
        console.log("Detail: " + this.props.food)
    }

    render(){

        if (this.props.food == undefined) {
            return <div />
        }
        return(
          <div>
              <h1>{this.props.food.Name}</h1>
              <ul>
                  {
                      this.props.food.ingrediences.map(i =>
                        <li>i.name</li>
                      )
                  }
              </ul>
              <ul>
                  {
                      this.props.food.steps.map(s =>
                          <li>s</li>
                      )
                  }
              </ul>
          </div>
        );
    }
}

export default Detail;
