import React from 'react';
import PropTypes from 'prop-types';
import styles from './Form.module.css';

class Form extends React.Component{
    constructor(props){
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);

        this.state = {
            message: "",
            newbook: {
                key: Date.now(),
                Title: "",
                Author: "",
                Genre :"",
                YearReleased: ""
            }
        };
    }

    handleSubmit(e) {
        console.log("Submit");
        e.preventDefault();
        this.setState({ message: 'Sending...' }, this.sendFormData);
    }

    sendFormData() {
        var formData = {
          Name: "Šlehačka",
          Category: "sladkost",
          Ingrediences: [{
              Amount: 250,
              Unit: "ml",
              Name: "smetana ke šlehání"
          }],
          Steps: ["ušlehejte", "zamrazte"]
        };
        setTimeout(() => {
            this.setState({ message: 'data sent!' });
            let obj = JSON.stringify(formData)
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: obj
            };
        }, 3000);
    }

    render(){
        return(
        <div>
            <form>
                <div className="form-group row">
                    <label className="col-2">Název</label>
                    <div className="col-10">
                        <input type="email" className="form-control"/>
                    </div>
                </div>
                <fieldset className="form-group">
                    <div className="row">
                        <legend className="col-form-label col-sm-2 pt-0">Radios</legend>
                        <div className="col-sm-10">
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="category" value="polevka"/>
                                <label className="form-check-label">Polévka</label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="category" value="hlavni"/>
                                <label className="form-check-label">Hlavní chod</label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="category" value="dezert"/>
                                <label className="form-check-label">Dezert</label>
                            </div>
                        </div>
                    </div>
                </fieldset>
                <div className="row">
                    <div className="col-3">
                        <h3>Ingredience:</h3>
                        <div className="form-group row">
                            <label className="col-2">1</label>
                            <input type="text" className="form-control col-8"/>
                        </div>
                        <div className="form-group row">
                            <label className="col-2">2</label>
                            <input type="text" className="form-control col-8"/>
                        </div>
                        <div className="form-group row">
                            <label className="col-2">3</label>
                            <input type="text" className="form-control col-8"/>
                        </div>
                        <button type="submit" className="btn btn-success">Přidat ingredienci</button>
                    </div>
                    <div className="col-9">
                        <h3>Postup:</h3>
                            <div className="form-group">
                                <label htmlFor="exampleInputEmail1">Krok 1</label>
                                <input type="email" className="form-control"/>
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputEmail1">Krok 2</label>
                                <input type="email" className="form-control"/>
                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputEmail1">Krok 3</label>
                                <input type="email" className="form-control"/>
                            </div>
                        <div className="form-group">
                            <button type="submit" className="btn btn-success">Přidat krok</button>
                        </div>
                    </div>
                </div>
                <button type="submit" className="btn btn-primary">Vytvořit</button>
            </form>
        </div>
        );
    }
}

export default Form;
