import React from 'react';
import PropTypes from 'prop-types';
import styles from './Detail.module.css';
import {getDetail} from "../../api";
import {Link, Route, Switch} from "react-router-dom";
import Form from "../Form/Form";
import List from "../List/List";

class Detail extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            food: {}
        }
        this.load = this.load.bind(this);
        this.load();
    }

    load(){
        getDetail(this.props.match.params.name).then(data =>
            this.setState({food: data})
        )
    }

    render(){

        if (this.state.food == undefined) {
            return <div />
        }
        return(
          <div>
              <h1>{this.state.food.Name}</h1>
              <h3>{this.state.food.Category}</h3>
              <p>{this.state.food.Description}</p>
              <ul>
                  {
                      this.state.food.ingrediences != undefined &&
                      this.state.food.ingrediences.map(i =>
                        <li>i.name</li>
                      )
                  }
              </ul>
              <ul>
                  {
                      this.state.food.steps != undefined &&
                      this.state.food.steps.map(s =>
                          <li>s</li>
                      )
                  }
              </ul>
              <Link to="/edit" className="btn btn-sm btn-outline-dark">Upravit</Link>
              <Switch>
                  <Route path="/edit/:name" render={() => <Form food={this.state.food} />} />
              </Switch>
          </div>
        );
    }
}

export default Detail;
