import React from 'react';
import PropTypes from 'prop-types';
import styles from './IngredienceFormGroup.module.css';
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faTimes} from "@fortawesome/free-solid-svg-icons";

const IngredienceFormGroup = ({index, remove, handleChange}) => (
    <div className="form-group row">
        <input type="text" className="form-control col-4" placeholder="název" data-id={index} name="name" onChange={handleChange}/>
        <input type="text" className="form-control col-3" placeholder="množství" data-id={index} name="amount" onChange={handleChange}/>
        <select name="unit" className="form-control col-2">
            <option>g</option>
            <option>ks</option>
            <option>ml</option>
            <option>dl</option>
            <option>l</option>
            <option>lžíce</option>
            <option>lžička</option>
        </select>
        <button id={index} className="btn-sm btn-danger" onClick={remove}><FontAwesomeIcon id={index} icon={faTimes}/></button>
    </div>
);

IngredienceFormGroup.propTypes = {};

IngredienceFormGroup.defaultProps = {};

export default IngredienceFormGroup;
