import React from 'react';
import PropTypes from 'prop-types';
import styles from './FoodForm.module.css';

class FoodForm extends React.Component{
    constructor(props){
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);

        this.state = {
            message: "",
            newbook: {
                key: Date.now(),
                Title: "",
                Author: "",
                Genre :"",
                YearReleased: ""
            }
        };
    }

    handleSubmit(e) {
        console.log("Submit");
        e.preventDefault();
        this.setState({ message: 'Sending...' }, this.sendFormData);
    }

    sendFormData() {
        var formData = {
            Title: this.Title.value,
            Author: this.Author.value,
            Genre: this.Genre.value,
            YearReleased: this.YearReleased.value};
        setTimeout(() => {
            //console.log(formData);
            this.setState({ message: 'data sent!' });
            let obj = JSON.stringify(formData)
            console.log(obj)
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: obj
            };
            fetch('/add', requestOptions)
                .then(response => response.json())
                .then(data => console.log("response: " + data.message));
        }, 3000);
    }

    render(){
        return(
        <div>
            <form onSubmit={this.handleSubmit}>
                    <p><label htmlFor="title">Title</label><input id="title" type="text" ref="Title"/></p>
                    <p><label htmlFor="author">Author</label><input id="author" type="text" ref="Author"/></p>
                    <p><label htmlFor="genre">Genre</label><input id="genre" type="text" ref="Genre"/></p>
                    <p><label htmlFor="first_published">First Published</label><input id="first_published" type="text"
                                                                                      ref="YearReleased"/></p>
                    <p><input type="submit"/></p>
            </form>
        </div>)
    }
}

export default FoodForm;
