import React from 'react';
import PropTypes from 'prop-types';
import styles from './Form.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCoffee } from '@fortawesome/free-solid-svg-icons';

class Form extends React.Component{
    constructor(props){
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.addIngredienceField = this.addIngredienceField.bind(this);
        this.removeIngredienceField = this.removeIngredienceField.bind(this);

        this.state = {
            food: {
                name: "",
                category: "",
                ingrediences: [],
                steps: []
            }
        };
    }

    handleSubmit(e) {
        console.log("Submit");
        e.preventDefault();
        this.setState({ message: 'Sending...' }, this.sendFormData);
    }

    handleChange(e){

    }

    addIngredienceField(e){

    }

    removeIngredienceField(e){

    }

    addStepField(e){}
    removeStepField(e){}

    sendFormData() {
        var formData = {
          Name: "Šlehačka",
          Category: "sladkost",
          Ingrediences: [{
              Amount: 250,
              Unit: "ml",
              Name: "smetana ke šlehání"
          }],
          Steps: ["ušlehejte", "zamrazte"]
        };
        setTimeout(() => {
            this.setState({ message: 'data sent!' });
            let obj = JSON.stringify(formData)
            const requestOptions = {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: obj
            };
        }, 3000);
    }

    render(){
        return(
        <div>
            <form>
                <div className="form-group row">
                    <label className="col-2">Název</label>
                    <div className="col-10">
                        <input type="text" className="form-control" name="name" />
                    </div>
                </div>
                <fieldset className="form-group">
                    <div className="row">
                        <legend className="col-form-label col-sm-2 pt-0">Radios</legend>
                        <div className="col-sm-10">
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="category" value="polevka"/>
                                <label className="form-check-label">Polévka</label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="category" value="hlavni"/>
                                <label className="form-check-label">Hlavní chod</label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="category" value="dezert"/>
                                <label className="form-check-label">Dezert</label>
                            </div>
                        </div>
                    </div>
                </fieldset>
                <div className="row">
                    <div className="col-5">
                        <h3>Ingredience:</h3>
                        <div className="form-group row">
                            <input type="text" className="form-control col-4" placeholder="název"/>
                            <input type="text" className="form-control col-3" placeholder="množství"/>
                            <select className="form-control col-2">
                                <option>g</option>
                                <option>ks</option>
                                <option>ml</option>
                                <option>dl</option>
                                <option>l</option>
                                <option>lžíce</option>
                                <option>lžička</option>
                            </select>
                            <button className="btn-sm btn-danger">Zrušit</button>
                        </div>
                        <div className="form-group row">
                            <input type="text" className="form-control col-4" placeholder="název"/>
                            <input type="text" className="form-control col-3" placeholder="množství"/>
                            <select className="form-control col-2">
                                <option>g</option>
                                <option>ks</option>
                                <option>ml</option>
                                <option>dl</option>
                                <option>l</option>
                                <option>lžíce</option>
                                <option>lžička</option>
                            </select>
                            <button className="btn-sm btn-danger">Zrušit</button>
                        </div>
                        <div className="form-group row">
                            <input type="text" className="form-control col-4" placeholder="název"/>
                            <input type="text" className="form-control col-3" placeholder="množství"/>
                            <select className="form-control col-2">
                                <option>g</option>
                                <option>ks</option>
                                <option>ml</option>
                                <option>dl</option>
                                <option>l</option>
                                <option>lžíce</option>
                                <option>lžička</option>
                            </select>
                            <button className="btn-sm btn-danger">Zrušit</button>
                        </div>
                        <button className="btn btn-success" onChange={this.addIngredienceField}>
                            <FontAwesomeIcon icon="check-square" />
                        </button>
                    </div>
                    <div className="col-7">
                        <h3>Postup:</h3>
                            <div className="form-group">
                                <label>Krok 1</label>
                                <textarea className="form-control"/>
                            </div>
                            <div className="form-group">
                                <label>Krok 2</label>
                                <textarea className="form-control"/>
                            </div>
                            <div className="form-group">
                                <label>Krok 3</label>
                                <textarea className="form-control"/>
                            </div>
                        <div className="form-group">
                            <button className="btn btn-success" onChange={this.addStepField}>Přidat krok</button>
                        </div>
                    </div>
                </div>
                <button type="submit" className="btn btn-primary">Vytvořit</button>
            </form>
        </div>
        );
    }
}

export default Form;
