async function callApi(path){
    /*fetch(path)
        .then(async response => {
            const data = await response.json();
            //console.log("JSON: " + data.content);
            return data.content;
        });*/
    let response = await fetch(path);
    let data = await response.json()
    return data;
}

export async function getFoods(){
    let data = await callApi("/food");
    return data;
}

export async function getDetail(id){
    let data = await callApi("/detail/"+ {id});
    return data;
}

export function getFilterFoods(name, categories, ingredience){
    let data = getFoods();
    if (name != null)
        data = getByName(data, name);
    if (categories != null)
       data = getByCategory(data, categories);
    //if (ingredience)
}

/**Filter functions */

export function getByName(data, name){
    return data.forEach( d => {
        if(d.name == name)
            return d;
    })
}

export function getByCategory(data, categories){
    return data.filter(d => {
        if(categories.include(d.category))
            return d;
    })
}

export function getByIngredience(data, ingredience){
    let len = ingredience.length;
    return data.filter(d => {
        var n = 0;
        ingredience.forEach(i => {
            if(i.ingrediences.includes(i))
                n++;
        })
        if(n == len)
            return d;
    })
}
